statline(key,val){return key+": "+val+"<br>"}
statlineMinMax(obj,key,title=null){var min=0;var max=0;var sorted=obj.sort(function(a,b){var av=a[key];var bv=b[key];if(av<bv)return-1;if(av>bv)return 1;return 0});if(sorted[0]){min=sorted[0][key];max=sorted[sorted.length-1][key]}
if(!title){title=key}
var text=title+": Between "+min+" and "+max+"<br>";if(min==max){text=title+": "+min+"<br>"}
return text}
tick(){for(var e in this.microbes){if(!this.microbes[e].tick()){this.stats.deaths+=1;this.microbes.splice(e,1)}}
for(var e in this.foods){if(!this.foods[e].tick()){this.foods.splice(e,1)}}
if(this.stats.counter%this.foodchance()==0&&this.stats.manualfed>0){this.foods.push(new Food(this));this.stats.autofed++}}
foodchance(){var res=this.microbes.length;res+=this.foods.length/2
res+=this.foodmod;if(res<1){res=1}
return Math.floor(res)}
